mapper.update:
update labusing
set labusing_date = #{labusingDate,jdbcType=TIMESTAMP},
  labusing_dateend = #{labusingDateend,jdbcType=TIMESTAMP},
  course_id = #{courseId,jdbcType=VARCHAR},
  laboratory_number = #{laboratoryNumber,jdbcType=VARCHAR}
where labusing_id = #{labusingId,jdbcType=VARCHAR}

mapper.select:
select labusing_id, labusing_date, labusing_dateend, course_id, laboratory_number
from labusing
where labusing_id = #{labusingId,jdbcType=VARCHAR}

select labusing_id, labusing_date, labusing_dateend, course_id, laboratory_number
from labusing
where course_id = #{courseCode,jdbcType=VARCHAR}

select labusing_id, labusing_date, labusing_dateend, course_id, laboratory_number from labusing where course_id = #{courseCode,jdbcType=VARCHAR}
and DATE_FORMAT(labusing_date,'%Y-%m-%d') >= DATE_FORMAT(#{starttime}, '%Y-%m-%d')
and DATE_FORMAT(labusing_date,'%Y-%m-%d') <= DATE_FORMAT(#{endtime},'%Y-%m-%d')
