mapper.insert:
insert into collective (collective_id, department_id, collective_number,tutoringUser,mentorUser
  )
values (#{collectiveId,jdbcType=VARCHAR}, #{departmentId,jdbcType=VARCHAR}, #{collectiveNumber,jdbcType=INTEGER},#{tutoringUser,jdbcType=INTEGER}
  ,#{mentorUser,jdbcType=INTEGER})

mapper.update:
update collective
set department_id = #{departmentId,jdbcType=VARCHAR},
  collective_number = #{collectiveNumber,jdbcType=INTEGER},
  tutoringUser = #{tutoringUser,jdbcType=INTEGER},
  mentorUser =#{mentorUser,jdbcType=INTEGER}
where collective_id = #{collectiveId,jdbcType=VARCHAR}

mapper.select:
select collective_id, department_id, collective_number,tutoringUser,mentorUser
from collective
where collective_id = #{collectiveId,jdbcType=VARCHAR}
